layout.script:
local function onChange(node, attribute, oldValue) showMessage(oldValue)
end
local function alternarFeiticos()
if self.rclMagias.visible == true then self.botao.text = "Lista Completa" self.rclMagias.visible = false self.testezim.visible = true else self.botao.text = "Lista Simplificada" self.rclMagias.visible = true self.testezim.visible = false end
end
local function alternarAudio() local node = NDB.getRoot(sheet)
if self.sound.text == "ðŸ”Š" then self.sound.text = "ðŸ”‡" node.soundControl = "desligado" else self.sound.text = "ðŸ”Š" node.soundControl = "ligado" end
end
local function adicionarFeitico()
local node = self.rclMagias:append();
node.nome = "Importar" node.escola = "-" node.tipo = "-" node.Efeito = "-"
node.Grad = 0 node.Grad_name = "Grad" node.Grad_grad = 1 node.Grad_array = {0}
node.CD = "-" node.CD_name = "CD" node.CD_grad = 1 node.CD_array = {0}
node.Dano = "-" node.Dano_name = "Dano" node.Dano_grad = 1 node.Dano_array = {"-"}
node.Poder = "-" node.Poder_name = "Poder" node.Poder_grad = 1 node.Poder_array = {"-"}
node.Range = "-" node.Range_name = "Range" node.Range_grad = 1 node.Range_array = {"-"}
node.Area = "-" node.Area_name = "Area" node.Area_grad = 1 node.Area_array = {"-"}
node.Duracao = "-" node.Duracao_name = "Duracao" node.Duracao_grad = 1 node.Duracao_array = {"-"}
end

recordList.event:
local node = self.rclMagias.selectedNode;
self.boxDetalhesDoItem.node = node;
self.boxDetalhesDoItem.visible = (node ~= nil);


if (tonumber(nodeA.Order) or 0) < (tonumber(nodeB.Order) or 0) then
      return -1;
elseif (tonumber(nodeA.Order) or 0) > (tonumber(nodeB.Order) or 0) then
      return 1;
else   
      return Utils.compareStringPtBr(nodeA.nome, nodeB.nome);
end;

